<div class="container" id="why_choose">
  <div id="why_choose">
    <div class="no_warp main-carousel" data-flickity='{ "cellAlign": "left", "contain": true , "freeScroll": true, "pageDots": false, "watchCSS":true}'>
      {% for item in why_choose %}
        {% assign parts = item | split: "|" %}
        {% assign title = parts[0] %}
        {% assign discription = parts[1] %}
        {% assign image = parts[2] %}
        {%- if forloop.length > 0 -%}
        <div class="box carousel-cell">
          <div class="box_left">
            <img src="{{ shop.url }}/cdn/shop/files/{{ image }}" alt="{{ title }}" />
          </div>
          <div class="box_right">
            <h3>{{ title }}</h3>
            <p>{{ discription }}</p>
          </div>
        </div>
        {%- endif -%}
      {% endfor %}
    </div>
  </div>
</div>

