settings.custom_liquid:
{{ 'section-featured-products.css' | asset_url | stylesheet_tag }}
{{ 'component-badge.css' | asset_url | stylesheet_tag }}
{{ 'component-price.css' | asset_url | stylesheet_tag }}
{{ 'component-card.css' | asset_url | stylesheet_tag }}
{%- if settings.quickview_show == false and settings.show_color_swatch -%}
  {{ 'section-main-product.css' | asset_url | stylesheet_tag }}
{% endif %}


{%- if settings.quickview_show or settings.add_to_cart_enabled -%}
  <link
    rel="preload"
    href="{{ 'section-main-product.css' | asset_url }}"
    as="style"
    onload="this.onload=null;this.rel='stylesheet'"
  >
  <link rel="stylesheet" href="{{ 'component-deferred-media.css' | asset_url }}" media="all">
  <link
    rel="preload"
    href="{{ 'quick-add.css' | asset_url }}"
    as="style"
    onload="this.onload=null;this.rel='stylesheet'"
  >

  <script src="{{ 'quick-add.js' | asset_url }}" defer="defer"></script>
  <script src="{{ 'product-form.js' | asset_url }}" defer="defer"></script>
{%- endif -%}


{% assign collection1 = collections['mum'] %}
<div class="container">
    <ul
      class="featured-products__wrapper featured-products__wrapper-{{ section.settings.columns_mobile }}-mobile list-unstyled"
    >
{% for product in collection1.products %}
        <li
          class="
            collection-product-card
            quickview{%- if settings.quickview_hover -%}--hover{% endif %}
          "
        >
          <style data-shopify="">
            .badge--soldout {
              color: rgb(var(--color-badge-soldout-text));
              background-color: rgb(var(--color-badge-soldout-background));
            }

            .badge--sale {
              color: rgb(var(--color-badge-sale-text));
              background-color: rgb(var(--color-badge-sale-background));
            }
          </style>
          {% render 'product-card',
            media_size: square,
            media_fit: cover,
            product_card_product: product,
            show_secondary_image: "true",
            show_vendor: false,
            show_desc: false,
            show_sku: false,
            section_id:event_template__featured-products
          %}
        </li>
      {%- else -%}
        {%- for i in (1..4) -%}
          <li class="collection-product-card">
            {% render 'product-card-placeholder', media_size: 'square' %}
          </li>
        {%- endfor -%}
      {%- endfor -%}
    </ul>
</div>
