<div class="container" id="message_data">
  <div class="product__outer">
    <div class="no_warp" >
      <div class="button-container">
        <button class="category-button active" data-category="all">All</button>
        {% assign categories = "" %}
        {% for item in message_data %}
          {% assign parts = item | split: "|" %}
          {% assign category_id = parts[0] %}
          {% assign category = parts[1] %}
          {% unless categories contains category %}
            <button class="category-button" data-category="{{category_id}}">{{ category }}</button>
            {% assign categories = categories | append: category | append: "," %}
          {% endunless %}
        {% endfor %}
      </div>
      <div class="message-container">
      {% for item in message_data %}
        {% assign parts = item | split: "|" %}
        {% assign category_id = parts[0] %}
        {% assign category = parts[1] %}
        {% assign text_id = parts[2] %}
        {% assign text = parts[3] %}
        {%- if forloop.length > 0 -%}
          <div category="{{category}}" category_id= "{{category_id}}" text_id="{{text_id}}" id="{{text}}" class="message_box">
            <p class="category"><span>{{category}}</span></p>
            <span class="message" for="{{text_id}}">{{ text }}</span><i for="{{text_id}}">Click to Copy</i>
          </div>
        {%- endif -%}
      {% endfor %}
      </div>
    </div>
  </div>
</div>
