settings.custom_liquid:
{% if product.compare_at_price_max > product.price %}
	<div class="sale_badge">
		{{ product.compare_at_price_max | minus: product.price | times: 100 | divided_by: product.compare_at_price_max | money_without_currency | times: 100  | remove: '.0'}}%  Off</div>
	<div class="saving_badge">
		Saving ${{ product.compare_at_price_max | minus: product.price | money_without_currency}}
	</div>
{% endif %}
